class Solution:
    # https://leetcode.com/problems/edit-distance/submissions/
    def dpeditdistance(self,s1,s2):
        h, w = len(s1)+1, len(s2)+1
        edMatrix = [[None] * (w) for _ in range(h)]
        for i in range(h):
            for j in range(w):
                if i == 0: edMatrix[i][j] = j
                elif j == 0: edMatrix[i][j] = i
                elif s1[i-1] == s2[j-1]: edMatrix[i][j] = edMatrix[i-1][j-1]
                else:
                    # edMatrix[i][j] = min(1+edMatrix[i][j-1], 1+edMatrix[i-1][j], 1+edMatrix[i-1][j-1]) # 1 if can replace
                    edMatrix[i][j] = min(1+edMatrix[i][j-1], 1+edMatrix[i-1][j]) # 2 if have to delete, then insert
        return edMatrix[len(s1)][len(s2)]
    def dpeditdistance(self,s1,s2):
        if not s1 and not s2: return 0
        if not s1: return len(s2)
        if not s2: return len(s1)
        cur = [len(s2)]; pre = [j for j in range(len(s2)+1)] # s2 length
        for i in range(1,len(s1)+1):
            cur = [i] + [0]*len(s2)
            for j in range(1,len(s2)+1):
                if s1[i-1] == s2[j-1]: cur[j] = pre[j-1]
                else: cur[j] = min(1+cur[j-1], 1+pre[j], 2+pre[j-1])
                # else: cur[j] = 1+ min(cur[j-1], pre[j], pre[j-1])
            pre = cur
        return cur[-1]
    # https://www.lintcode.com/problem/one-edit-distance/description
    def isOneEditDistance(self, s1: str, s2: str) -> int:
        h, w = len(s1)+1, len(s2)+1
        prevRow = [i for i in range(w)]
        for i in range(1,h):
            curRow = [i for _ in range(w)]
            for j in range(1,w):
                if s1[i-1] == s2[j-1]:
                    curRow[j] = prevRow[j-1]
                else:
                    curRow[j] = min(1+curRow[j-1], 1+prevRow[j], 1+prevRow[j-1])
            prevRow = curRow
        return prevRow[-1] == 1
    def minDistance(self, word1, word2):
        h, w = len(word1)+1, len(word2)+1
        pre = [i for i in range(w)]
        cur = [i for i in range(w)]
        for i in range(1, h):
            cur[0] = i
            for j in range(1, w):
                cur[j] = min(pre[j-1]+(word1[i-1] != word2[j-1]), pre[j]+1, cur[j-1]+1)
            pre = cur
        return pre[-1]
    def minDistance(self, word1, word2):
        h, w = len(word1)+1, len(word2)+1
        pre = [i for i in range(w)]
        cur = [0]*w
        for i in range(1, h):
            cur[0] = i
            for j in range(1, w):
                cur[j] = min(pre[j-1]+(word1[i-1] != word2[j-1]), pre[j]+1, cur[j-1]+1)
            cur, pre = pre, cur
        return pre[-1] == 1
    # remove at most k characters to transform into a palindrome
    def dpkpalindrome(self,s, k):
        midpt = (len(s)+1)//2
        return self.dpeditdistance(s[:midpt], s[midpt:][::-1]) <= k
if __name__ == "__main__":
    sol = Solution()
    tdata = ["",
""]
    # print(sol.minDistance("adb","aDb"))
    assert sol.isOneEditDistance("adb","aDb") == 1
    assert sol.dpeditdistance("adb","aDb") == 2 # 2 for delete and insert, 1 for replace
    # print(sol.minDistance(*tdata))
    # print(sol.isOneEditDistance(*tdata))
    # print(sol.dpeditdistance(*tdata))
    print(sol.dpeditdistance("intrinsic","intrusive"))
    assert sol.dpkpalindrome("gdd",1) == True
    assert sol.dpkpalindrome("phmjjmap",1) == False
    assert sol.dpkpalindrome("ivjwvi",1) == False
    assert sol.dpkpalindrome("epyyevdadveyype",1) == True
    assert sol.dpkpalindrome("abxa",1) == False
    assert sol.dpkpalindrome("abccaa",1) == False
    assert sol.dpkpalindrome("abdxa",1) == False
    assert sol.dpkpalindrome("abaxbabax",1) == False
